import { useState } from "react";

export default function Form({ onAddItems }) {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);

  function handleSubmit(e) {
    e.preventDefault();

    if (!description) return; //false Í∞Ä Îê† Ïàò ÏûàÎäî Í∞í : "", 0, false

    const newItem = { description, quantity, packed: false, id: Date.now() };
    console.log(newItem);

    onAddItems(newItem);

    setDescription("");
    setQuantity(1);
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your üòç trip?</h3>
      <select
        value={quantity}
        //   onChange={(e) => setQuantity(e.target.value)}
        // target.valueÎäî String ÏúºÎ°ú Î∞òÌôòÎêòÍ∏∞ ÎïåÎ¨∏Ïóê Ïà´ÏûêÍ∞Ä Î¨∏ÏûêÏó¥Î°ú Î≥ÄÌôòÎêúÎã§.
        // Ïà´ÏûêÎ•º ÏÇ¨Ïö©Ìï† Í≤ÉÏù¥ÎùºÎ©¥ Îëê Í∞ÄÏßÄ Î∞©Î≤ïÏùÑ ÏÇ¨Ïö©Ìï† Ïàò ÏûàÎã§.
        // Î¨∏ÏûêÏó¥Î°ú ÎßåÎì§Îïå "" + Î•º Ï∂îÍ∞ÄÌï¥Ï£ºÎäî Í≤É Ï≤òÎüº + Î•º ÏÇ¨Ïö©Ìï† Ïàò ÏûàÍ≥†,
        // js Ïùò Number() Î•º ÏÇ¨Ïö©Ìï† ÏàòÎèÑ ÏûàÎã§. Îçî Î™ÖÏãúÏ†ÅÏúºÎ°ú ÌëúÌòÑÌïòÍ∏∞ ÏúÑÌï¥ Number() ÏÇ¨Ïö©ÌïòÎèÑÎ°ù ÌïòÏûê.
        onChange={(e) => setQuantity(Number(e.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((i) => (
          <option value={i} key={i}>
            {i}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="item..."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <button>Add</button>
    </form>
  );
}
