import { useState } from "react";

const initialItems = [
  { id: 1, description: "Passports", quantity: 2, packed: true },
  { id: 2, description: "Socks", quantity: 12, packed: false },
];

export default function App() {
  return (
    <div className="app">
      <Logo />
      <Form />
      <PackingList />
      <Stats />
    </div>
  );
}

function Logo() {
  return <h1>üèùÔ∏è Far Away üß≥</h1>;
}

function Form() {
  const [description, setDescription] = useState("");
  const [quantity, setQuantity] = useState(1);
  const [items, setItems] = useState([]);

  function handleAddItems(item) {
    //useState Í∞Ä const Î°ú ÏÑ†Ïñ∏ÎêòÏñ¥ÏûàÍ∏∞ ÎïåÎ¨∏Ïóê items ÏïàÏóê add ÌïòÎäî ÌñâÏúÑÎäî Ìï† Ïàò ÏóÜÎã§.
    //ÏÉà Î∞∞Ïó¥ÏùÑ ÎßåÎì§Ïñ¥ÏÑú ÎåÄÏ≤¥Ìï¥Ï£ºÏñ¥ÏïºÌïúÎã§.
    setItems((items) => [...items, item]);
  }

  function handleSubmit(e) {
    e.preventDefault();

    if (!description) return; //false Í∞Ä Îê† Ïàò ÏûàÎäî Í∞í : "", 0, false

    const newItem = { description, quantity, packed: false, id: 3 };
    console.log(newItem);

    handleAddItems(newItem);

    setDescription("");
    setQuantity(1);
  }

  return (
    <form className="add-form" onSubmit={handleSubmit}>
      <h3>What do you need for your üòç trip?</h3>
      <select
        value={quantity}
        //   onChange={(e) => setQuantity(e.target.value)}
        // target.valueÎäî String ÏúºÎ°ú Î∞òÌôòÎêòÍ∏∞ ÎïåÎ¨∏Ïóê Ïà´ÏûêÍ∞Ä Î¨∏ÏûêÏó¥Î°ú Î≥ÄÌôòÎêúÎã§.
        // Ïà´ÏûêÎ•º ÏÇ¨Ïö©Ìï† Í≤ÉÏù¥ÎùºÎ©¥ Îëê Í∞ÄÏßÄ Î∞©Î≤ïÏùÑ ÏÇ¨Ïö©Ìï† Ïàò ÏûàÎã§.
        // Î¨∏ÏûêÏó¥Î°ú ÎßåÎì§Îïå "" + Î•º Ï∂îÍ∞ÄÌï¥Ï£ºÎäî Í≤É Ï≤òÎüº + Î•º ÏÇ¨Ïö©Ìï† Ïàò ÏûàÍ≥†,
        // js Ïùò Number() Î•º ÏÇ¨Ïö©Ìï† ÏàòÎèÑ ÏûàÎã§. Îçî Î™ÖÏãúÏ†ÅÏúºÎ°ú ÌëúÌòÑÌïòÍ∏∞ ÏúÑÌï¥ Number() ÏÇ¨Ïö©ÌïòÎèÑÎ°ù ÌïòÏûê.
        onChange={(e) => setQuantity(Number(e.target.value))}
      >
        {Array.from({ length: 20 }, (_, i) => i + 1).map((i) => (
          <option value={i} key={i}>
            {i}
          </option>
        ))}
      </select>
      <input
        type="text"
        placeholder="item..."
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />
      <button>Add</button>
    </form>
  );
}

function PackingList() {
  return (
    <div className="list">
      <ul>
        {initialItems.map((item) => (
          <Item item={item} key={item.id} />
        ))}
      </ul>
    </div>
  );
}

function Item({ item }) {
  return (
    <li>
      <span style={item.packed ? { textDecoration: "line-through" } : {}}>
        {item.quantity} {item.description}
      </span>
      <button>‚ùå</button>
    </li>
  );
}

function Stats() {
  return (
    <div className="stats">
      üå¥ You have X items on your list, and you already packed X (X%)
    </div>
  );
}
